"use client";
import * as React from "react";
import { Slot } from "@radix-ui/react-slot";

import { cn } from "@/lib/utils";
import { type VariantProps } from "class-variance-authority";
import { buttonVariants } from "./variants";

// ----------------------------------------------------------------------

export interface FloatingLabelButonProps
	extends Omit<React.ButtonHTMLAttributes<HTMLButtonElement>, "color" | "value">,
		VariantProps<typeof buttonVariants> {
	asChild?: boolean;
	ring?: boolean;
	// variant?: "destructive" | "secondary" | "link" | "ghost" | "soft" | "filled" | "outline";
	color?: "default" | "primary" | "secondary" | "info" | "success" | "warning" | "error";
	size?: "default" | "sm" | "md" | "lg" | "icon";
	startIcon?: React.ReactNode;
	endIcon?: React.ReactNode;

	label: string;
	value: React.ReactNode;
	error?: boolean;
}

const FloatingLabelButon = React.forwardRef<HTMLButtonElement, FloatingLabelButonProps>(
	({ className, color, size, asChild = false, error = false, startIcon, endIcon, label, value, ...props }, ref) => {
		const labelRef = React.useRef<React.ElementRef<"span">>(null);
		const [btnMinWidth, setBtnMinWidth] = React.useState(60);
		const Comp = asChild ? Slot : "button";

		React.useEffect(() => {
			if (!!labelRef.current) {
				const minW = !!startIcon || !!endIcon ? labelRef.current.clientWidth + 24 : labelRef.current.clientWidth;
				setBtnMinWidth(minW);
			}
		}, [startIcon, endIcon]);

		return (
			<Comp
				data-start-icon={!!startIcon}
				data-end-icon={!!endIcon}
				style={{
					minWidth: `calc(1.5rem + ${btnMinWidth}px)`,
				}}
				className={cn(buttonVariants({ color, size, className }), "justify-start relative group border-0 h-12", {
					"text-error": !!error,
				})}
				type="button"
				ref={ref}
				{...props}
				data-value={!!value}>
				{!!startIcon && <span className="flex-shrink-0 mr-1">{startIcon}</span>}
				<span
					ref={labelRef}
					className={cn(
						"absolute text-muted-foreground transition-all group-data-[value=true]:-translate-y-4 group-data-[value=true]:top-2 group-data-[value=true]:text-xs flex-grow flex-shrink-0",
						{
							"group-data-[value=true]:-translate-x-1 translate-x-7": !!startIcon,
						},
					)}>
					{label}
				</span>
				{!!value ? <span className="text-left">{value}</span> : <span className="w-full" />}
				{!!endIcon && <span className="flex-shrink-0 ml-auto">{endIcon}</span>}
				<fieldset
					className={cn(
						"absolute group-focus:border-2 group-focus-visible:border-2 transition-all group-focus:border-primary group-focus-visible:border-primary inset-0 -top-[5px] border dark:border-input/35 border-input rounded-md m-0 py-0 text-left px-2 pointer-events-none min-w-0 group-focus:[&>legend]:max-w-full group-focus-visible:[&>legend]:max-w-full group-data-[value=false]:[&>legend]:max-w-0",
						{
							"text-error border-error": !!error,
						},
					)}>
					<legend className="transition-all invisible whitespace-nowrap overflow-hidden w-auto max-w-full h-3 leading-4 text-xs font-normal p-0">
						<span className="px-1 visible inline-block opacity-0">{label}</span>
					</legend>
				</fieldset>
			</Comp>
		);
	},
);

FloatingLabelButon.displayName = "FloatingLabelButton";

export { FloatingLabelButon };

import { cva } from "class-variance-authority";

// ----------------------------------------------------------------------

export const buttonVariants = cva(
	"inline-flex items-center justify-center capitalize whitespace-nowrap overflow-ellipsis rounded-md font-medium transition-colors focus-visible:outline-none disabled:pointer-events-none disabled:opacity-35 transition-colors px-3",
	{
		compoundVariants: [
			// Default color
			{
				variant: "filled",
				color: "default",
				className: "",
			},
			{
				variant: "outline",
				color: "default",
				className: "text-muted-foreground dark:border-input/35 border-input/65 border",
			},
			{
				variant: "ghost",
				color: "default",
				className: "text-foreground dark:hover:bg-accent/15 hover:bg-accent",
			},
			{
				variant: "soft",
				color: "default",
				className:
					"text-foreground/95 dark:bg-accent/15 bg-accent/75 hover:bg-foreground/15 dark:hover:bg-accent/35 hover:text-foreground",
			},
			{
				variant: "link",
				color: "default",
				className: "text-default",
			},
			// Primary color
			{
				variant: "filled",
				color: "primary",
				className: "text-white bg-primary hover:bg-primary-dark",
			},
			{
				variant: "outline",
				color: "primary",
				className: "text-primary border-primary border",
			},
			{
				variant: "ghost",
				color: "primary",
				className: "text-primary hover:bg-primary/15",
			},
			{
				variant: "soft",
				color: "primary",
				className: "text-primary bg-primary/15 hover:bg-primary/35",
			},
			{
				variant: "link",
				color: "primary",
				className: "text-primary",
			},
			// Secondary color
			{
				variant: "filled",
				color: "secondary",
				className: "text-white bg-secondary hover:bg-secondary-dark",
			},
			{
				variant: "outline",
				color: "secondary",
				className: "text-secondary border-secondary border",
			},
			{
				variant: "ghost",
				color: "secondary",
				className: "text-secondary hover:text-secondary hover:bg-secondary/15",
			},
			{
				variant: "soft",
				color: "secondary",
				className: "text-secondary bg-secondary/15 hover:bg-secondary/35",
			},
			{
				variant: "link",
				color: "secondary",
				className: "text-secondary",
			},
			// Info color
			{
				variant: "filled",
				color: "info",
				className: "text-white bg-info hover:bg-info-dark",
			},
			{
				variant: "outline",
				color: "info",
				className: "text-info border-info border",
			},
			{
				variant: "ghost",
				color: "info",
				className: "text-info hover:bg-info/15",
			},
			{
				variant: "soft",
				color: "info",
				className: "text-info bg-info/15 hover:bg-info/35",
			},
			{
				variant: "link",
				color: "info",
				className: "text-info",
			},
			// Success color
			{
				variant: "filled",
				color: "success",
				className: "text-white bg-success hover:bg-success-dark",
			},
			{
				variant: "outline",
				color: "success",
				className: "text-success border-success border",
			},
			{
				variant: "ghost",
				color: "success",
				className: "text-success hover:bg-success/15",
			},
			{
				variant: "soft",
				color: "success",
				className: "text-success bg-success/15 hover:bg-success/35",
			},
			{
				variant: "link",
				color: "success",
				className: "text-success",
			},
			// Warning color
			{
				variant: "filled",
				color: "warning",
				className: "text-white bg-warning hover:bg-warning-dark",
			},
			{
				variant: "outline",
				color: "warning",
				className: "text-warning border-warning border",
			},
			{
				variant: "ghost",
				color: "warning",
				className: "text-warning hover:bg-warning/15",
			},
			{
				variant: "soft",
				color: "warning",
				className: "text-warning bg-warning/35 hover:bg-warning/15",
			},
			{
				variant: "link",
				color: "warning",
				className: "text-warning",
			},
			// Error color
			{
				variant: "filled",
				color: "error",
				className: "text-white bg-error hover:bg-error-dark",
			},
			{
				variant: "outline",
				color: "error",
				className: "text-error border-error border",
			},
			{
				variant: "ghost",
				color: "error",
				className: "text-error hover:bg-error/15",
			},
			{
				variant: "soft",
				color: "error",
				className: "text-error bg-error/15 hover:bg-error/35",
			},
			{
				variant: "link",
				color: "error",
				className: "text-error",
			},
		],
		variants: {
			variant: {
				destructive: "bg-destructive text-destructive-foreground hover:bg-destructive/90",
				secondary: "bg-secondary text-secondary-foreground hover:bg-secondary/80",
				link: "underline-offset-4 hover:underline",
				ghost: "",
				filled: "",
				outline: "",
				soft: "",
			},
			color: {
				default: "text-foreground",
				primary: "",
				secondary: "",
				info: "",
				success: "",
				warning: "",
				error: "",
			},
			size: {
				default: "h-12 text-md",
				sm: "h-9 text-sm",
				md: "h-14 text-base py-4",
				lg: "h-11 text-lg",
				icon: "h-10 text-sm w-10",
			},
		},
		defaultVariants: {
			variant: "filled",
			size: "md",
			color: "default",
		},
	},
);
